<?php require( dirname( __FILE__ ) . '/wp-blog-header.php' ); ?><?php $video_ryad = $_GET['video_ryad'];?><?php wp_reset_query(); $wp_query = new WP_Query(array(   'post_type' => 'video_review',   'post_status' => 'publish',   'caller_get_posts'=> 1)   );$i = 1; $j = 0;while ($wp_query->have_posts()) : $wp_query->the_post(); ?><?php $ip =  $video_ryad*4; ?><?php if ($i > $ip) { ?><div class="col-md-3 col-sm-6 col-xs-12">    <div class="video-frame">        <iframe class="video-frame" src="<?php echo (get_post_meta($post->ID, 'ссылка', true)); ?>" frameborder="0" allowfullscreen></iframe>        </div>    <p>        <img src="<?php echo get_template_directory_uri(); ?>/img/user-icon.png" alt="">        <span><?php echo iconv("UTF-8", "WINDOWS-1251", get_the_title()); ?></span>        <span class="date"><? the_time('d.m.Y'); ?></span>    </p></div><?php $j++; ?><?php } ?><?php $i++; ?><?php if ($j>3) {    break;} ?><?php endwhile; ?><?php wp_reset_query(); ?>